<template>
    <div class="containerr"> 
        <img :src="imgSrc" alt="" class="productImage">        
        <div class="title">
            {{title}}
        </div>  
        <div class="addToCart" @click="toCart">
            {{isElInCart}}
        </div>
        <div class="price">
            <div v-if="currency==='UAH'">
                {{price | uahPrice}}
            </div>
            <div v-if="currency==='USD'">
                {{price | dolPrice}}
            </div>
        </div>
    </div>
</template>

<script>
    import dolPrice from "@/filters/dolPrice";
    import uahPrice from "@/filters/uahPrice";
    import { mapGetters } from "vuex";
    export default {
        name: "Product",

        props: {
            imgSrc: {
                type: String,
                default: "data:image/png;base64,"
            },
            
            price: {
                type: [String, Number],
                default: null
            },

            title: {
                type: String,
                default: "Product Name"
            },

            isElInCart: {
                type: String,
                default: 'В корзину'
            },
        },

        filters: {
            dolPrice,
            uahPrice,
        },

        computed: {
            ...mapGetters(['currency'])
        },

        methods: {
            toCart() {
                this.$emit('to-cart')
            }
        },
    }
</script>

